function setColorScheme(a){if(a==="dark"){document.head.getElementsByTagName("link").item(1).href="assets/dark.min.css"}else{if(a==="light"){document.head.getElementsByTagName("link").item(1).href="assets/light.min.css"}}}function refreshColorScheme(){let cs=localStorage.getItem("colorScheme");if(cs===null){cs="dark"}setColorScheme(cs);localStorage.setItem("colorScheme",cs)}function toggleColorScheme(){let cs=localStorage.getItem("colorScheme");if(cs==="dark"){cs="light"}else{if(cs==="light"){cs="dark"}}setColorScheme(cs);localStorage.setItem("colorScheme",cs)}refreshColorScheme();document.getElementById("color-scheme-toggle").addEventListener("click",toggleColorScheme);